options.customCss:
/* 1) Import Oswald for header typography */
@import url('https://fonts.googleapis.com/css2?family=Oswald:wght@400;700&display=swap');

:root {
  /* Colors */
  --chat--color-primary: #e31b23;
  --chat--color-primary-shade-50: #c11a1f;
  --chat--color-primary-shade-100: #9f141b;
  --chat--color-secondary: #171717;
  --chat--color-secondary-shade-50: #0f0f0f;
  --chat--color-white: #ffffff;
  --chat--color-light: #f2f4f8;
  --chat--color-light-shade-50: #e6e9f1;
  --chat--color-light-shade-100: #c2c5cc;
  --chat--color-medium: #d2d4d9;
  --chat--color-dark: #0d0d0d;
  --chat--color-disabled: #777980;
  --chat--color-typing: #404040;

  /* Layout & Typography */
  --chat--spacing: 1rem;
  --chat--border-radius: 0.5rem;
  --chat--transition-duration: 0.2s;
  --chat--font-family: ('Oswald','Roboto','Segoe UI',sans-serif);
  --chat--heading--font-size: 2.5em;
  --chat--subtitle--font-size: inherit;
  --chat--subtitle--line-height: 2;

  /* Window */
  --chat--window--width: 400px;
  --chat--window--height: 600px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: none;
  --chat--window--box-shadow: 0 4px 20px rgba(0,0,0,0.4);
  --chat--window--border-radius: var(--chat--border-radius);
  --chat--window--margin-bottom: var(--chat--spacing);

  /* Header */
  --chat--header-height: auto;
  --chat--header--padding: var(--chat--spacing);
  --chat--header--background: var(--chat--color-secondary);
  --chat--header--color: var(--chat--color-white);
  /* darker red here: */
  --chat--header--border-bottom: 4px solid var(--chat--color-primary-shade-50);

  /* Messages */
  --chat--message--font-size: 1rem;
  --chat--message--padding: var(--chat--spacing);
  --chat--message--border-radius: var(--chat--border-radius);
  --chat--message-line-height: 1.5;
  --chat--message--margin-bottom: var(--chat--spacing);
  --chat--message--bot--background: var(--chat--color-light);
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--pre--background: rgba(0,0,0,0.05);
  --chat--messages-list--padding: var(--chat--spacing);

  /* Toggle */
  --chat--toggle--size: 64px;
  --chat--toggle--border-radius: 50%;
  --chat--toggle--background: var(--chat--color-primary);
  --chat--toggle--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--active--background: var(--chat--color-primary-shade-100);
  --chat--toggle--color: var(--chat--color-white);

  /* Input & Buttons */
  --chat--textarea--height: 50px;
  --chat--textarea--max-height: 30rem;
  --chat--input--font-size: inherit;
  --chat--input--border: 0;
  --chat--input--padding: 0.8rem;
  --chat--input--background: var(--chat--color-white);
  --chat--input--line-height: 1.5;
  --chat--input--placeholder--font-size: var(--chat--input--font-size);
  --chat--input--border-active: 0;
  --chat--input--left--panel--width: 2rem;

  --chat--button--color: var(--chat--color-light);
  --chat--button--background: var(--chat--color-primary);
  --chat--button--padding: 0.5rem var(--chat--spacing);
  --chat--button--border-radius: var(--chat--border-radius);
  --chat--button--hover--background: var(--chat--color-primary-shade-50);

  /* Send button → BLACK */
  --chat--input--send--button--background: #000000;
  --chat--input--send--button--color: var(--chat--color-white);
  --chat--input--send--button--background-hover: #000000;
  --chat--input--send--button--color-hover: var(--chat--color-white);

  /* File button stays brand-light */
  --chat--input--file--button--background: var(--chat--color-white);
  --chat--input--file--button--color: var(--chat--color-secondary);
  --chat--input--file--button--color-hover: var(--chat--color-secondary-shade-50);
  --chat--files-spacing: 0.25rem;

  /* Body & Footer */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-light);
  --chat--footer--color: var(--chat--color-dark);
}

/* Ensure all headers use Oswald + primary red */
header h1,
header h2,
div[class*="header"] h1,
div[class*="header"] h2,
.chathub__header__title,
.chathub__header__subtitle,
.chat-header .heading,
.chat-header .subtitle,
.chat-window .header h1,
.chat-window .header h2 {
  font-family: 'Oswald', sans-serif !important;
  color: var(--chat--color-primary) !important;
  margin: 0 !important;
}

/* Heading size */
header h1,
.chathub__header__title,
.chat-header .heading,
.chat-window .header h1 {
  font-size: var(--chat--heading--font-size) !important;
}

/* Subtitle tweaks */
header h2,
.chathub__header__subtitle,
.chat-header .subtitle,
.chat-window .header h2 {
  font-size: 2rem !important;
  opacity: 0.9 !important;
  margin-top: 0.25em !important;
}

/* Bubble width */
.chat-message {
  max-width: 50%;
}

