  json: {
    chatInput: item.json.chatInput,
    sessionId: item.json.sessionId
  }
}));

const output = [];
let counter = 1; // Always starts at 1

for (const item of $input.all()) {
  // Parse the nested questions JSON string
  const data = JSON.parse(item.json.questions);

  output.push({
    json: {
      QuestionID: `Q-${String(counter).padStart(3, '0')}`,
      Question: data.question,
      Section: data.section,
      Type: data.type || 'N/A',
      Response: data.response
    }
  });

  counter++;
}

return output;

