let counter = 1;

for (const item of $input.all()) {
  // Parse the single JSON object stored as a string
  const req = JSON.parse(item.json.requirements);

  // Push one item per input entry
  output.push({
    json: {
      RequirementID: `REQ-${String(counter).padStart(3, '0')}`,
      Requirement: req.topic || 'N/A',
      Section: req.section || 'N/A',
      Priority: req.priority || 'N/A',
      ComplianceMethod: req.complianceMethod || 'N/A',
      Response: req.response || 'N/A',
    }
  });

  counter++;
}

return output;

